cf:definition.core:text:
// determine Zone
int zone = Math.floor(easting / 1000000.0);
String code = "EPSG:25832"

if (zone == 31) {
  code = "EPSG:25831"
}
else if (zone == 33) {
  code = "EPSG:25833"
}

def x = easting - (zone * 1000000.0)
def y = northing

_.geom.with(geometry: "POINT($x $y)", crs: code)

complexParameter.core:text:
if (ADZ) {

_target {
	LocatorDesignator {
		type {
			href( 'http://inspire.ec.europa.eu/codelist/LocatorDesignatorTypeValue/addressNumberExtension' )
		}
		designator( ADZ )
	}
}

}
