def c = _.context.collector()

def obereGewaesserAchseIDs = c.crossing[_source.p.id.value()].values()

if (obereGewaesserAchseIDs) {
	obereGewaesserAchseIDs.each{ obereGewaesserAchseID ->
		_source.links.AX_Fliessgewaesser.p.id.each { fliessgewaesserID ->
			fliessgewaesserID = 'WatercourseLink_' + fliessgewaesserID
			_target {
			//https://inspire.ec.europa.eu/data-model/approved/r4618-ir/html/EARoot/EA3/EA1/EA5/EA9018.htm: "The order reflects their elevation; the first link is the lower link. "
				element {
					//unteres Fliessgewaesser
					href( "#$fliessgewaesserID" )
					}
				element {
					// obere Gewaesserachse/Fliessgewaesser
					href( "#WatercourseLink_$obereGewaesserAchseID" )
				}
			}
		}
	}
}

