// Ermitteln, ob flächenhafte (Aus AX_Fliessgewaesser) und/oder linienhafte (aus AX_Gewaesserachse) Geometrien vorhanden sind 

// Builder konfigurieren
// - Zuweisung der Geometrie auf Ebene von geometry zulassen
_b.strictValueFlags = false
def _ns = _project.vars.INSPIRE_NAMESPACE

int counter = 1;

boolean hatLinienhaft = !!_source.links.AX_Gewaesserachse.p.position.first()
boolean hatFlaechenhaft = !!_source.links.AX_Fliessgewaesser.p.position.first()


def kanalId = _source.p.id.value()
if (!hatLinienhaft && !hatFlaechenhaft) {
	_log.warn("Watercourse: Kanal $kanalId hat weder AX_Fliessgewaesser noch AX_Gewaesserachsen")
	throw new NoResultException('Kann kein Watercourse-Objekt ohne Geometrie erstellen')
}


def createWatercourse = { aggregate -> 
	if (aggregate) {
		def localID = 'Watercourse_' + kanalId + '_' + counter++
		_target {
			geometry(aggregate)
			id( localID )
			inspireId {
				Identifier {
					namespace( _ns )
					localId( localID )
				}
			}
			identifier( _ns ? (_ns.endsWith('/') ? _ns : _ns + '/') + localID : localID ) {
				codeSpace( 'http://inspire.ec.europa.eu/ids' )
			}
		}
	}
	else {
		_log.warn("Watercourse: Für Kanal $kanalId konnte kein Aggregat gebildet werden.")
	}
}

// erstelle Watercourse
if (hatLinienhaft) {
	def aggregate = _.geom.aggregate(_.geom.findAll(_source.links.AX_Gewaesserachse.p.position.list()))
	def geoms = _.geom.toSimpleGeometries(geometries: aggregate, collections: false)
	if (geoms.size() > 1) {
		_log.warn("${geoms.size()} Geometrien für Kanal $kanalId (aus Gewaesserachsen)")
	}
	geoms.each { geom ->
		createWatercourse(geom)
	}
}
if (hatFlaechenhaft) {
	def aggregate = _.geom.union(_.geom.findAll(_source.links.AX_Fliessgewaesser.p.position.list()))
	def geoms = _.geom.toSimpleGeometries(geometries: aggregate, collections: false)
	if (geoms.size() > 1) {
		_log.warn("${geoms.size()} Geometrien für Kanal $kanalId (aus Fliessgewaessern)")
	}
	geoms.each { geom ->
		createWatercourse(geom)
	}
}

// Ermitteln, ob flächenhafte (Aus AX_Fliessgewaesser) und/oder linienhafte (aus AX_Gewaesserachse) Geometrien vorhanden sind 

// Builder konfigurieren
// - Zuweisung der Geometrie auf Ebene von geometry zulassen
_b.strictValueFlags = false
def _ns = _project.vars.INSPIRE_NAMESPACE

int counter = 1;

boolean hatLinienhaft = !!_source.links.AX_Gewaesserachse.p.position.first()
boolean hatFlaechenhaft = !!_source.links.AX_Fliessgewaesser.p.position.first()


def wasserlaufId = _source.p.id.value()
if (!hatLinienhaft && !hatFlaechenhaft) {
	_log.warn("Watercourse: Wasserlauf $wasserlaufId hat weder AX_Fliessgewaesser noch AX_Gewaesserachsen")
	throw new NoResultException('Kann kein Watercourse-Objekt ohne Geometrie erstellen')
}

def breiteMax
def breiteMin

if (hatLinienhaft){	
	_source.links.AX_Gewaesserachse.each {
		achse ->
		def value = achse.p.breiteDesGewaessers.value()
		if (value){	
			if (!breiteMax || value > breiteMax){
				breiteMax = value
			}
			if (!breiteMin || value < breiteMin){
				breiteMin = value
			}
		}
		
	}
	
	switch (breiteMin){
		case 3:
			breiteMin = 0
			break
		case 6:
			breiteMin = 3
			break
		case 12:
			breiteMin = 6
			break
		case 18:
			breiteMin = 12
			break
		case 30:
			breiteMin = 18
			break
		case 42:
			breiteMin = 30
			break
		default: 
			breiteMin = 0
			_log.warn ("Für Watercourse konnte keine minimale Breite ermittelt werden. Es wurde der Wert 0 m zugewiesen.")
	}

	if(!breiteMax){
		_log.warn ("Keine maximale Breita da")
	}
}

else {
	breiteMax = 10000
	breiteMin = 10000
}

def createWatercourse = { aggregate -> 
	if (aggregate) {
		def localID = 'Watercourse_' + wasserlaufId + '_' + counter++
		_target {
			geometry(aggregate)
			id( localID )
			inspireId {
				Identifier {
					namespace( _ns )
					localId( localID )
				}
			}
			identifier( _ns ? (_ns.endsWith('/') ? _ns : _ns + '/') + localID : localID ) {
				codeSpace( 'http://inspire.ec.europa.eu/ids' )
			}
			width {
				WidthRange {
					lower( breiteMin ) {
						uom( 'm' )
					}
					upper( breiteMax ) {
						uom( 'm' )
					}
				}
			}
		}
	}
	else {
		_log.warn("Watercourse: Für Wasserlauf $wasserlaufId konnte kein Aggregat gebildet werden.")
	}
}

// erstelle Watercourse
if (hatLinienhaft) {
	def aggregate = _.geom.aggregate(_.geom.findAll(_source.links.AX_Gewaesserachse.p.position.list()))
	def geoms = _.geom.toSimpleGeometries(geometries: aggregate, collections: false)
	if (geoms.size() > 1) {
		_log.warn("${geoms.size()} Geometrien für Wasserlauf $wasserlaufId (aus Gewaesserachsen)")
	}
	geoms.each { geom ->
		createWatercourse(geom)
	}
}
if (hatFlaechenhaft) {
	def aggregate = _.geom.union(_.geom.findAll(_source.links.AX_Fliessgewaesser.p.position.list()))
	def geoms = _.geom.toSimpleGeometries(geometries: aggregate, collections: false)
	if (geoms.size() > 1) {
		_log.warn("${geoms.size()} Geometrien für Wasserlauf $wasserlaufId (aus Fliessgewaessern)")
	}
	geoms.each { geom ->
		createWatercourse(geom)
	}
}

