def flaechen = _.geom.findPolygons(position)
	
def flaeche = _.geom.aggregate(flaechen)
	
if(flaeche){
	def oberflaeche = flaeche.geometry.getArea()
	return oberflaeche
}

def linien = _.geom.findLines(position)

def linie = _.geom.aggregate(linien)

if(linie){
	def laenge = linie.geometry.getLength()
	return laenge
}

