_source.p.weistAuf.each { instance ->
	c.weistAuf[instance.p.href.value() as String] << _source.p.flurstueckskennzeichen.value()
	//_log.info(instance.p.href.value() as String)
	//_log.info(_source.p.flurstueckskennzeichen.value())
}

_source.p.zeigtAuf.each { instance ->
	c.zeigtAuf[instance.p.href.value() as String] << _source.p.flurstueckskennzeichen.value()
}

//Das ist eine Dummy Groovy Retype-Funktion. Sie dient dazu, alle relevanten Informationen aus AX_Gebaeude in einem Kollektor zu sammeln, die für den Verweis auf ein Gebäude in Address.building benötigt werden.

def c = _.context.collector()

//dieser Kollektor betrifft die inverse Relationsrichtung und muss daher lt. Kommentar von Herrn Weichand zur entsprechenden hale connect-Aufgabe nicht berücksichtigt werden
//c.gebaeude[_source.p.identifier.value()] << _source.p.id.value()

_source.p.zeigtAuf.each { instance ->
	c.gebZeigtAuf[instance.p.href.value() as String] << _source.p.id.value()
}

def positionAdresse = _source.links.AX_GeoreferenzierteGebaeudeadresse.p.position.first()
def pointId = ""
def c = _.context.collector()
if (!positionAdresse) {
	positionAdresse = _source.links.AP_PTO.p.position.first()
	if (positionAdresse) {
		pointId = positionAdresse.p.Point.id.value()
		positionAdresse = _.geom.find(positionAdresse)
	}
}
if (!positionAdresse) {
	positionAdresse = _source.links.AX_Gebaeude.p.position.first()
	if (positionAdresse) {
		positionAdresse = _.geom.centroid(_.geom.find(positionAdresse))
	}
}

def parcels = new HashSet()
//lt. Kommentar Herr Weichand zu entpsrechender Aufgabe in haleconnect wird diese Richtung des Verweises nicht genutzt
/*_source.p.gehoertZu.each {
