		buildings << c.gebaeude[instance.p.href.value()].value()
	}
}*/
c.gebZeigtAuf[ _source.p.identifier.value() as String].each{ value ->
	buildings << value
}

if (positionAdresse) {
	_target {
		position {
			GeographicPosition {
				specification {
					href( 'http://inspire.ec.europa.eu/codelist/GeometrySpecificationValue/building' )
					title( 'building' )
				}
				geometry {
					Point(_.geom.find(positionAdresse))
					{
						if (pointId) {
							id(pointId)
						}
					}
				}
				method {
					href( 'http://inspire.ec.europa.eu/codelist/GeometryMethodValue/byAdministrator' )
					title( 'by administrator' )
				}
				'default'( 'true' )
			}
		}
		parcels.each{ value ->
			parcel {
				href(  _project.vars.CROSSTHEME_CADASTRALPARCELS_URL + 'CadastralParcel_' + value )
			}
		}
		buildings.each{ value ->
			building {
				href(  _project.vars.CROSSTHEME_BUILDINGS_URL + 'Building_' + value )
			}
		}
	}
}

