surfaces.each { surface ->
	// ID bestimmen
	def _id = "RoadArea_${sourceId}_${num}"
	def _ns = _project.vars.INSPIRE_NAMESPACE

	// Ziel-Objekt erstellen mit IDs und Geometrie
