			Identifier {
				localId( _id )
				namespace( _ns )
			}
		}
		identifier( _ns ? (_ns.endsWith('/') ? _ns : _ns + '/') + _id : _id ) {
			codeSpace( 'http://inspire.ec.europa.eu/ids' )
		}
		geometry(surface)
	}

	// Sammle ID für Netzwerk
	collect.network << _id
	// Sammle ID für FunctionalRoadClass
	if (functionalClass) {
		collect.functionalClass[functionalClass] << _id
	}
	// Sammle ID für VerticalPosition
	collect.verticalPosition[verticalPosition] << _id

	num++
}

