//{land}_{gemarkungsnummer}_{nuzmmerierungsbezirk}_{nummerDesGrablochs}
def land = kennziffer_AX_KennzifferGrabloch_land
def gemnr = kennziffer_AX_KennzifferGrabloch_gemarkungsnummer
def numbez = kennziffer_AX_KennzifferGrabloch_nummerierungsbezirk
def numgl = kennziffer_AX_KennzifferGrabloch_nummerDesGrablochs


if (land && gemnr && numbez) {
	return land + '_' + gemnr + '_' + numbez + '_' + numgl
}

else if (land && gemnr){
	return land + '_' + gemnr + '__' + numgl
}

else if (land && numbez){
	return land + '__' + numbez + '_' + numgl
}

else if (gemnr && numbez){
	return '_' + gemnr + '_' + numbez + '_' + numgl
}
